package main

import (
	"fmt"
	"strings"
)

func main() {
	// Santa starts at floor 0
	// ( open perenthesis up one floor
	// ) close perenthesis down one floor
	// basement possible

	// examples:
	// (()) is up two floors, down two floors. Results in floor 0
	// ))((((( is down two floors, up 5 floors, Results in floor 3

	// Part One
	// Question: To what floor do the instructions take Santa?
	fmt.Printf("The instructions take Santa to the %s floor\n", partOne())

	// Question: What is the position of the character that causes Santa to first enter the basement?
	fmt.Printf("The position of the character that causes Santa to first enter the basement is: %s\n", partTwo())
}

func partOne() string {
	floorDown := strings.Count(input(), "(")
	floorUp := strings.Count(input(), ")")

	floorNumber := floorDown - floorUp

	return fmt.Sprint(floorNumber)
}

func partTwo() string {
	floorNumber := 0
	for i := range strings.Count(input(), "") {
		if input()[i] == 40 {
			floorNumber++
		} else if input()[i] == 41 {
			floorNumber--
		}
		if floorNumber == -1 {
			return fmt.Sprint(i + 1) // +1 because i starts at 0
		}
	}
	return "Something went wrong"
}

